import './App.css';
import { sampleData } from './backend';
import { CsvToHtmlTable } from 'react-csv-to-table';
import Papa from "papaparse";
import { bootstrap } from "bootstrap/dist/css/bootstrap.css"
import { type } from '@testing-library/user-event/dist/type';

function App() {
  return (
    <div className="App" style={{ display: "flex", justifyContent: "center" }}>
      <Notes />

    </div>
  );
}

function Notes() {
  const backendData = [
    { title: "Grocery List", description: "Milk, Soup, Bread", createdat: "01-18-2021" },
    { title: "Math Homework", description: "Remember to finish question 8-10 before monday", createdat: "12-01-2020" },
    { title: "Call James", description: "Ask him about the company party.", createdat: "12-30-2020" }
  ]

  const noteRootStyle = {
    border: "2px #0af solid",
    borderRadius: 9,
    margin: 20,
    backgroundColor: "#efefef",
    padding: 9
  };
  return (
    <div >
      <CsvToHtmlTable
        data={sampleData}
        csvDelimiter=","
        tableClassName="table table-striped table-dark"
      />
    </div>
  )
}
function Csvtojson() {
  return (
    <div className="App">
      <input
        type="file"
        accept=".csv,.xlsx,.xls"
        onChange={(e) => {
          const files = e.target.files;
          console.log(files);
          if (files) {
            console.log(files[0]);
            Papa.parse(files[0], {
              complete: function (results) {
                console.log("Finished:", results.data);
              }
            }
            )
          }
        }}
      />
    </div>
  );
}

export default App;

