  const [query, setQuery] = useState("");
  const keys = ["customerID", "companyName", "contactName", "contactTitle", "address", "city", "region", "postalCode", "country", "phone", "fax"];
  console.log(sampleData[0]["companyName"])
  const search = (data) => {
    return data.filter(
      item => item.customerID.toLowerCase().includes(query) || item.companyName.toLowerCase().includes(query)
      // item =>
      //   keys.some((key) => (item[key].toLowerCase().includes(query)))
    );
  };
  return (
    <>
      <div className='container' Style="margin:30px">
        <input type="text" placeholder='Search...' className="search" onChange={(e) => setQuery(e.target.value)} />
      </div>
      <div >
        <Table data={search(sampleData)} />
      </div>
    </>
  );
}

