import { sampleData } from './backend';
import "bootstrap/dist/css/bootstrap.css"
import { React, useState } from 'react';
import Select from 'react-select'
import Table from './table';


function App() {
  const [query, setQuery] = useState("");
  const options = [
    { value: 'Select', label: 'Select' },
    { value: 'customerID', label: 'customerID' },
    { value: 'companyName', label: 'companyName' },
    { value: 'contactName', label: 'contactName' }
  ]

  const MyComponent = () => (
    <Select options={options} onChange={(e) => {
      options.map(opt => { if (e.value === opt.value) { setKeys([opt.value]) } })
      // if (e.value === options[0].value) { setKeys(["customerID"]) }
      // else setKeys(["customerID", "companyName", "contactName", "contactTitle", "address", "city", "region", "postalCode", "country", "phone", "fax"]);
    }} />
  )

  const [keys, setKeys] = useState(["customerID", "companyName", "contactName", "contactTitle", "address", "city", "region", "postalCode", "country", "phone", "fax"]);
  const keysForSearch = ["customerID", "companyName", "contactName", "contactTitle", "address", "city", "region", "country", "phone", "fax"];

  const search = (data) => {
    return data.filter(
      item =>
        keysForSearch.some((key) => (item[key].toLowerCase().includes(query)))
    );
  };

  return (
    <>
      {/* header */}
      <div style={{ "color": "blue", fontSize: 40, fontWeight: "bold", textAlign: 'center' }} > atlan</div>

      {/* dropDown */}
      <div Style="margin:45px">
        {MyComponent()}
      </div>

      {/* Search bar */}
      <div className='container' Style="margin:30px">
        <input type="text" placeholder='Search...' className="search" onChange={(e) => setQuery(e.target.value)} />
      </div>

      {/* Table */}
      <div Style="margin:30px" >
        <Table data={search(sampleData)} keys={keys} />
      </div>
    </>
  );
}
export default App;
