import { sampleData } from './backend';
import "bootstrap/dist/css/bootstrap.css"
import { React, useState } from 'react';
import Table from './table';

function App() {
  const [query, setQuery] = useState("");
  const keys = ["customerID", "companyName", "contactName", "contactTitle", "address", "city", "region", "country", "phone", "fax"];
  const search = (data) => {
    return data.filter(
      item =>
        keys.some((key) => (item[key].toLowerCase().includes(query)))
    );
  };
  return (
    <>
      {/* header */}
      <div style={{ "color": "blue", fontSize: 40, fontWeight: "bold", textAlign: 'center' }} > atlan</div>
      {/* dropDown */}
      <div Style="margin:45px">
        <select>
          <option value="fruit">Fruit</option>
          <option value="vegetable">Vegetable</option>
          <option value="meat">Meat</option>
        </select>
      </div>
      {/* Search bar */}
      <div className='container' Style="margin:30px">
        <input type="text" placeholder='Search...' className="search" onChange={(e) => setQuery(e.target.value)} />
      </div>
      {/* Table */}
      <div Style="margin:30px" >
        <Table data={search(sampleData)} />
      </div>
    </>
  );
}

